#region Usings//Systemusing System;using System.Collections.Generic;//Xnausing Microsoft.Xna.Framework;using System.Security.Cryptography;#endregion //Usingsnamespace com.amazingcow.BowAndArrow{    public class YellowBalloon : Balloon    {        #region Constants         public const int kSpeedMinYellonBalloon = 45;        public const int kSpeedMaxYellowBallon  = 55;        #endregion        #region CTOR        public YellowBalloon() :             base()        {            //Init the Sprites.            _spriteList.Add(new Sprite("ballon_yellow"));            _spriteList.Add(new Sprite("ballon_yellow_dead"));            CurrentSprite = _spriteList[kSpriteIndexAlive];            //Init the Speed.            var ySpeed = GameManager.Instance.RandomNumGem.Next(kSpeedMinYellonBalloon,                                                                 kSpeedMaxYellowBallon);            Speed = new Vector2(0, -ySpeed);        }        #endregion //CTOR    }}