#region Usings//System using System;//XNAusing Microsoft.Xna.Framework;using Microsoft.Xna.Framework.Audio;#endregion //Usingsnamespace com.amazingcow.BowAndArrow{    public abstract class GameObject    {        #region Enums         public enum State         {            Alive,            Dying,            Dead         }        #endregion //Enums        #region Public Properties        public virtual Vector2 Position      { get; set; }         public virtual Vector2 Speed         { get; set; }        public virtual Sprite  CurrentSprite { get; protected set; }        public virtual State   CurrentState  { get; protected set; }        #endregion //Public Properties        #region CTOR         public GameObject()        {            CurrentSprite = null;            CurrentState  = State.Alive;        }        #endregion //CTOR         #region Update / Draw         public abstract void Update(GameTime gt);        public abstract void Draw(GameTime gt);        #endregion //Update / Draw         #region Public Methods         public abstract void Kill();        #endregion //Public Methods     }}